async function main({ params }: Args): Promise<Output> {
    try {
      const {
        font_size,
        in_animation,
        keyword_color,
        keyword_font_size,
        keywords,
        texts,
        timelines,
        out_animation,
        out_animation_duration
      } = params ?? {};

      // åŸºæœ¬æ ¡éªŒ
      if (!Array.isArray(texts) || !Array.isArray(timelines) || texts.length !== timelines.length || texts.length === 0) {
        throw new Error("texts ä¸ timelines æ•°é‡å¿…é¡»ä¸€è‡´ä¸”éç©º");
      }
      if (typeof keywords !== "string" || !keywords.trim()) {
        throw new Error("keywords å¿…é¡»ä¸ºéç©ºå­—ç¬¦ä¸²");
      }

      // æ‹†åˆ†å…³é”®è¯å¹¶å»é‡
      const kwList: string[] = [...new Set(keywords.split("|").map(s => s.trim()).filter(Boolean))];

      const infos = texts.map((text: string, idx: number) => {
        const { start, end } = timelines[idx] || {};
        if (typeof start !== "number" || typeof end !== "number") {
          throw new Error(`timelines ç¬¬ ${idx} é¡¹ start/end æ ¼å¼é”™è¯¯`);
        }
        const matched = kwList.filter(kw => text.includes(kw));
        return {
          start,
          end,
          text,
          keyword: matched.join("|"),
          keyword_color,
          keyword_font_size,
          font_size,
          in_animation,
